import React from 'react';
import FollowerCard from './FollowerCard';
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import Table from '@material-ui/core/Table';
import styled from 'styled-components';

const UserContainer = styled.div`
display: flex;
flex-direction: column;
`
const UserBox = styled.div`
display: flex;
flex-direction: column;
margin: 2rem;
`
const UserHeader = styled.h1`
text-align: center;
font-size: 3rem;
`
const UserImg = styled.img`
width:30%;
`
const UserInfo = styled.div`
display:flex;
flex-direction:row;
`
const UserText = styled.div`
display:flex;
flex-direction:column;
`
const FollowersBox = styled.div`
display: flex;
flex-direction:column;
`
const FollowerHeader = styled.h3`
text-align: center;
`
const UserCard = props => {
console.log('usercard props',props)
    return(
        <UserContainer className='userContainer'>
            <UserBox  className='userCard'>
                <UserHeader className='userHeader'>{props.user.name}</UserHeader>
                <UserInfo>
                    <UserImg className='userImg' alt='user' src={props.user.avatar_url}/>
                    <UserText>
                        <Typography paragraph='true'className='userBio'>{props.user.bio}</Typography>
                        <Typography className='userHandle'>GitHub Handle: {props.user.login}</Typography>
                        <Link color='primary' block='true' className='userLink' href={props.user.html_url}>{props.user.html_url}</Link>
                        <Typography gutterBottom='true'className='userRepo'>Public Repo's: {props.user.public_repos}</Typography>
                    </UserText>
                </UserInfo>
            </UserBox>
            <FollowersBox className='followerContainer'>
                <FollowerHeader className='userFollwerHeader'>Followers</FollowerHeader>
                <Table className='followerCards'>
                    {props.followers.map(fol => <FollowerCard setSearchUser={props.setSearchUser}  key={fol.id} login={fol.login} pic={fol.avatar_url} html_url={fol.html_url} />)}
                </Table>
            </FollowersBox>
        </UserContainer>
    )
}

export default UserCard;
