      user: {}
    }
  }
  componentDidMount() {
    fetch('https://api.github.com/users/SMoore3773')
    .then(response => {response.json()})
    .then(res => {this.setState({user: res})})
    .catch(err => console.log('catch error in api req',err))
  }


    render() {


