import React from 'react';
import FollowerCard from './FollowerCard';
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import Link from '@material-ui/core/Link';
import Table from '@material-ui/core/Table';
import Grid from '@material-ui/core/Grid';
import Styled from 'styled-components';

const userContainer = styled.div `
display: flex;
flex-direction: column;
`

const UserCard = props => {
console.log('usercard props',props)
    return(
        <userContainer className='userContainer'>
            <Grid  className='userCard'>
                <Typography align= 'center' variant='h1'className='userHeader'>{props.user.name}</Typography>
                <img className='userImg' alt='user' src={props.user.avatar_url}/>
                <Typography paragraph='true'className='userBio'>{props.user.bio}</Typography>
                <Typography className='userHandle'>GitHub Handle: {props.user.login}</Typography>
                <Link color='primary' block='true' className='userLink' href={props.user.html_url}>{props.user.html_url}</Link>
                <Typography gutterBottom='true'className='userRepo'>Public Repo's: {props.user.public_repos}</Typography>
            </Grid>
            <div className='followerContainer'>
                <h3 className='userFollwerHeader'>Followers</h3>
                <Table className='followerCards'>
                    {props.followers.map(fol => <FollowerCard setSearchUser={props.setSearchUser}  key={fol.id} login={fol.login} pic={fol.avatar_url} html_url={fol.html_url} />)}
                </Table>
            </div>
        </userContainer>
    )
}

export default UserCard;
